const buttonPopup=document.getElementById("button-open-popup"),popup=document.querySelector(".popup");export default function btnEventHandlers(){buttonPopup.onclick=()=>{popup.classList.toggle("open-popup"),document.body.scrollHeight>document.body.clientHeight&&document.body.classList.toggle("fixed-window")},document.onclick=o=>{o.target.closest("#button-open-popup")||o.target.closest(".popup")||(popup.classList.remove("open-popup"),document.body.classList.remove("fixed-window"))}}